@import "../styles/tokens.css";
@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

/* =========================
   SHADCN TOKEN BRIDGE
========================= */
@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);

  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);

  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);

  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);

  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);

  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);

  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);

  --color-destructive: var(--destructive);

  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);

  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --radius-2xl: calc(var(--radius) + 8px);

  /* Button Primary */
  --color-button-primary-default: var(--button-primary-default);
  --color-button-primary-hovered: var(--button-primary-hovered);
  --color-button-primary-pressed: var(--button-primary-pressed);
  --color-button-primary-selected: var(--button-primary-selected);
  --color-button-primary-focused: var(--button-primary-focused);

  /* Button Danger Primary */
  --color-button-danger-primary-default: var(--button-danger-primary-default);
  --color-button-danger-primary-hovered: var(--button-danger-primary-hovered);
  --color-button-danger-primary-pressed: var(--button-danger-primary-pressed);
  --color-button-danger-primary-selected: var(--button-danger-primary-selected);
  --color-button-danger-primary-focused: var(--button-danger-primary-focused);

  /* Button Danger Secondary */
  --color-button-danger-secondary-default: var(
    --button-danger-secondary-default
  );
  --color-button-danger-secondary-hovered: var(
    --button-danger-secondary-hovered
  );
  --color-button-danger-secondary-pressed: var(
    --button-danger-secondary-pressed
  );
  --color-button-danger-secondary-selected: var(
    --button-danger-secondary-selected
  );
  --color-button-danger-secondary-focused: var(
    --button-danger-secondary-focused
  );

  /* Button Neutral */
  --color-button-neutral-default: var(--button-neutral-default);
  --color-button-neutral-hovered: var(--button-neutral-hovered);
  --color-button-neutral-pressed: var(--button-neutral-pressed);
  --color-button-neutral-selected: var(--button-neutral-selected);
  --color-button-neutral-focused: var(--button-neutral-focused);

  /* Button Black */
  --color-button-black-default: var(--button-black-default);
  --color-button-black-hovered: var(--button-black-hovered);
  --color-button-black-pressed: var(--button-black-pressed);
  --color-button-black-selected: var(--button-black-selected);
  --color-button-black-focused: var(--button-black-focused);

  /* Button On-Color */
  --color-button-oncolor-default: var(--button-oncolor-default);
  --color-button-oncolor-hovered: var(--button-oncolor-hovered);
  --color-button-oncolor-pressed: var(--button-oncolor-pressed);
  --color-button-oncolor-selected: var(--button-oncolor-selected);
  --color-button-oncolor-focused: var(--button-oncolor-focused);

  /* Button Transparent */
  --color-button-transparent-default: var(--button-transparent-default);
  --color-button-transparent-hovered: var(--button-transparent-hovered);
  --color-button-transparent-pressed: var(--button-transparent-pressed);
  --color-button-transparent-selected: var(--button-transparent-selected);
  --color-button-transparent-focused: var(--button-transparent-focused);

  /* Text */
  --color-text-default: var(--foreground);

  /* Border */
  --color-border-error-light: var(--border-error-light);
  --color-border-neutral-primary: var(--border);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar: var(--sidebar);
}

/* =========================
   SYSTEM TOKENS
========================= */
:root {
  --radius: var(--radius-radius8);

  --background: var(--background-background-body);
  --foreground: var(--text-text-default);

  --card: var(--background-background-card);
  --card-foreground: var(--text-text-default);

  --popover: var(--background-background-menu);
  --popover-foreground: var(--text-text-default);

  --primary: var(--button-button-background-primary-default);
  --primary-foreground: var(--text-text-oncolor-primary);

  --secondary: var(--button-button-background-neutral-default);
  --secondary-foreground: var(--text-text-default);

  --muted: var(--background-background-neutral100);
  --muted-foreground: var(--text-text-secondary-paragraph);

  --accent: var(--background-background-primary50);
  --accent-foreground: var(--text-text-primary);

  --destructive: var(--button-button-background-danger-primary-default);

  --border: var(--border-border-neutral-primary);
  --input: var(--border-border-neutral-secondary);
  --ring: var(--border-border-primary);
  --border-error-light: var(--border-border-error-light);

  /* Button Primary Variant */
  --button-primary-default: var(--button-button-background-primary-default);
  --button-primary-hovered: var(--button-button-background-primary-hovered);
  --button-primary-pressed: var(--button-button-background-primary-pressed);
  --button-primary-selected: var(--button-button-background-primary-selected);
  --button-primary-focused: var(--button-button-background-primary-focused);

