  --button-danger-primary-hovered: var(
    --button-button-background-danger-primary-hovered
  );
  --button-danger-primary-pressed: var(
    --button-button-background-danger-primary-pressed
  );

  --button-danger-primary-selected: var(
    --button-button-background-danger-primary-selected
  );
  --button-danger-primary-focused: var(
    --button-button-background-danger-primary-focused
  );

  /* Button Danger Secondary Variant */
  --button-danger-secondary-default: var(
    --button-button-background-danger-secondary-default
  );
  --button-danger-secondary-hovered: var(
    --button-button-background-danger-secondary-hovered
  );
  --button-danger-secondary-pressed: var(
    --button-button-background-danger-secondary-pressed
  );
  --button-danger-secondary-selected: var(
    --button-button-background-danger-secondary-selected
  );
  --button-danger-secondary-focused: var(
    --button-button-background-danger-secondary-focused
  );

  /* Button Neutral Variant */
  --button-neutral-default: var(--button-button-background-neutral-default);
  --button-neutral-hovered: var(--button-button-background-neutral-hovered);
  --button-neutral-pressed: var(--button-button-background-neutral-pressed);
  --button-neutral-selected: var(--button-button-background-neutral-selected);
  --button-neutral-focused: var(--button-button-background-neutral-focused);

  /* Button Black Variant */
  --button-black-default: var(--button-button-background-black-default);
  --button-black-hovered: var(--button-button-background-black-hovered);
  --button-black-pressed: var(--button-button-background-black-pressed);
  --button-black-selected: var(--button-button-background-black-selected);
  --button-black-focused: var(--button-button-background-black-focused);

  /* Button On-Color Variant */
  --button-oncolor-default: var(--button-button-background-oncolor-default);
  --button-oncolor-hovered: var(--button-button-background-oncolor-hovered);
  --button-oncolor-pressed: var(--button-button-background-oncolor-pressed);
  --button-oncolor-selected: var(--button-button-background-oncolor-selected);
  --button-oncolor-focused: var(--button-button-background-oncolor-focused);

  /* Button Transparent Variant */
  --button-transparent-default: var(
    --button-button-background-transparent-default
  );
  --button-transparent-hovered: var(
    --button-button-background-transparent-hovered
  );
  --button-transparent-pressed: var(
    --button-button-background-transparent-pressed
  );
  --button-transparent-selected: var(
    --button-button-background-transparent-selected
  );
  --button-transparent-focused: var(
    --button-button-background-transparent-focused
  );

  /* Button Disabled */
  --button-disabled-on-color: var(--button-button-background-disabled-on-color);

  /* Button Icon/Label Colors */
  --button-label-transparent-hovered-on-color: var(
    --button-button-label-transparent-hovered-on-color
  );
  --button-label-transparent-pressed-on-color: var(
    --button-button-label-transparent-pressed-on-color
  );
  --button-label-transparent-selected-on-color: var(
    --button-button-label-transparent-selected-on-color
  );
  --button-icon-transparent-hovered-on-color: var(
    --button-button-icon-transparent-hovered-on-color
  );
  --button-icon-transparent-pressed-on-color: var(
    --button-button-icon-transparent-pressed-on-color
  );
  --button-icon-transparent-selected-on-color: var(
    --button-button-icon-transparent-selected-on-color
  );

  /* Link Icon Colors */
  --link-icon-primary-hovered: var(--link-link-icon-primary-hovered);
  --link-icon-primary-pressed: var(--link-link-icon-primary-pressed);
  --link-icon-primary-focused: var(--link-link-icon-primary-focused);
  --link-icon-primary-visited: var(--link-link-icon-primary-visited);

  /* Global Icon & Text Disabled */
  --icon-disabled: var(--global-icon-default-disabled);
  --icon-oncolor-disabled: var(--global-icon-default-oncolor-disabled);
  --text-disabled: var(--global-text-default-disabled);
  --text-oncolor-disabled: var(--global-text-default-oncolor-disabled);
  --background-disabled: var(--global-background-disabled);
  --sidebar: hsl(0 0% 98%);
  --sidebar-foreground: hsl(240 5.3% 26.1%);
  --sidebar-primary: hsl(240 5.9% 10%);
  --sidebar-primary-foreground: hsl(0 0% 98%);
  --sidebar-accent: hsl(240 4.8% 95.9%);
  --sidebar-accent-foreground: hsl(240 5.9% 10%);
  --sidebar-border: hsl(220 13% 91%);
  --sidebar-ring: hsl(217.2 91.2% 59.8%);
